import { createGlobalStyle,  } from 'styled-components';

const GlobalStyle = createGlobalStyle`  

   html{
      height: 100%;
      font-family: "Titillium Web", sans-serif;
   }
   *{
      margin: 0;
      padding: 0;
   }

`;

export { GlobalStyle };
